import { HouseService } from './../../shared/services/house.service';
import { AuthService } from './../../shared/services/auth.service';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { GlobalData } from '../../shared/utils/data';

@Component({
  selector: 'app-discover',
  templateUrl: './discover.component.html',
  styles: [],
})
export class DiscoverComponent implements OnInit {
  houseList: Array<any> = [];
  data: any = GlobalData;

  public filterData: any = {
    city: '',
    bed: '',
    bath: '',
    garage: '',
    minPrice: '',
    maxPrice: '',
  };



  constructor(
    private authService: AuthService,
    //  private houseService: HouseService,
    private activatedroute: ActivatedRoute,
    private router: Router
  ) {
    this.authService.checkIsLogedIn();
  }

  ngOnInit(): void {
    this.activatedroute.queryParams.subscribe((params) => {
      this.filterData = params;
    });
   // this.houseList = this.houseService.getHouseList(this.filterData);
  }

  setCity(value: any) {
    this.filterData.city = value;
  }
  setBed(value: any) {
    this.filterData.bed = value;
  }
  setBath(value: any) {
    this.filterData.bath = value;
  }
  setGarage(value: any) {
    this.filterData.garage = value;
  }
  setMaxPrice(value: any) {
    this.filterData.maxPrice = value;
  }
  setMinPrice(value: any) {
    this.filterData.minPrice = value;
  }

  searchButtonPress() {
    //console.log(this.filterData);
    this.router.navigateByUrl(
      '/' +
        `?city=${this.filterData.city}&bed=${this.filterData.bed}&bath=${this.filterData.bath}&garage=${this.filterData.garage}&min=${this.filterData.minPrice}&max=${this.filterData.maxPrice}`
    );
  }
}
