import { user } from './../models/user';
import { Injectable } from '@angular/core';
import { ToastrService } from 'ngx-toastr';
import {
  HttpClient,
  HttpHeaders,
  HttpParams,
  HttpErrorResponse,
} from '@angular/common/http';

import { Apis } from '../utils/apis';
import { GlobalData } from '../utils/data';
import { catchError } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  public isLogedin: boolean = false;
  public userData: any;
  private tokenRes: any = { token: '' };

  public headers: any = new HttpHeaders({'Content-Type': 'application/json; charset=utf-8',
    Authorization: localStorage.getItem('token')
  });

  constructor(private http: HttpClient, private toastr: ToastrService) {}

  login(userData: user) {
    this.http.post(Apis.login, userData).subscribe({
      next: (res) => {
        this.tokenRes = res;
        localStorage.setItem('token', 'bearer' + this.tokenRes.token);
      },
      error: (error) => {
        this.toastr.warning(error.error.message, '', {
          progressBar: true,
        });
      },
    });
  }

