import { Injectable, OnInit } from '@angular/core';
import { Apis } from '../utils/apis';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { user } from './../models/user';
import { ToastrService } from 'ngx-toastr';

@Injectable({
  providedIn: 'root',
})
export class UserService {
  public apiResponse: any = {
    status: '',
    message: '',
  };

  public userData: user = {
    name: '',
    phone: 0,
    email: '',
    password: '',
  };

  public headers: any = new HttpHeaders({
    'Content-Type': 'application/json; charset=utf-8',
    authorization: this.getToken(),
  });

  constructor(private authSe, private http: HttpClient, private toastr: ToastrService) {
    this.getUser();
  }

  getToken() {
    return localStorage.getItem('token') || '';
  }

  getUser() {
    this.http
      .get(Apis.user.getUserByToken, {
        headers: this.headers,
      })
      .subscribe({
        next: (res) => {
          this.userData = res as user;
        },
        error: (error) => {
          console.log(error.status);

          if(error.status == 401) 

          this.apiResponse = error.error;
          this.toastr.warning(this.apiResponse.message, '', {
            progressBar: true,
          });
        },
      });
  }
}
