import { UserService } from 'src/app/shared/services/user.service';
import { PropertyService } from './../../shared/services/property.service';
import { Component } from '@angular/core';
import { AuthService } from './../../shared/services/auth.service';
import { GlobalData } from '../../shared/utils/data';
import { property } from 'src/app/shared/models/property';
import { Router } from '@angular/router';

@Component({
  selector: 'app-sell',
  templateUrl: './sell.component.html',
  styles: [],
})
export class SellComponent {
  data: any = GlobalData;

  public imageFile: any;

  public property: property = {
    id: 0,
    name: '',
    area: 0,
    bed: 0,
    bath: 0,
    garage: 0,
    city: '',
    price: 0,
    user: '',
    active: true,
    image: '',
  };

  constructor(
    private authService: AuthService,
    private propertyService: PropertyService,
    private router: Router,
    private userService:UserService
  ) {
    this.authService.checkIsLogedIn();
  }

  // Setter for body
  setName(value: any) {
    this.property.name = value;
  }
  setCity(value: any) {
    this.property.city = value;
  }
  setBed(value: any) {
    this.property.bed = value;
  }
  setBath(value: any) {
    this.property.bath = value;
  }
  setGarage(value: any) {
    this.property.garage = value;
  }
  setArea(value: any) {
    this.property.area = value;
  }
  setPrice(value: any) {
    this.property.price = value;
  }
  setImage(value: string) {
    this.property.image = value;
  }

  setFile(value: any) {
    this.imageFile = value;
  }

  reloadCurrentRoute() {
    let currentUrl = this.router.url;
    this.router.navigateByUrl('/', { skipLocationChange: true }).then(() => {
      this.router.navigate([currentUrl]);
    });
  }

  uploadButtonClick() {
    this.property.user =
    this.propertyService.uploadProperty(this.property, this.imageFile);
    this.reloadCurrentRoute();
  }
}
