import { Apis } from './../../utils/apis';
import { HttpClient } from '@angular/common/http';
import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-file-input',
  templateUrl: './file-input.component.html',
  styles: [],
})
export class FileInputComponent {
  public imageSrc: any = '';
  @Input() file:any;

  public imageUrl: string = '';

  @Output() getFileEvent = new EventEmitter<any>();

  constructor(private http: HttpClient,     private errorService: ErrorService) {}

  public imageRes: any = {
    status: '',
    message: '',
    image: '',
  };

  uploadImage(image: any) {
    var formData = new FormData();
    formData.append('file', image);
    this.http.post(Apis.image.uploadImage, formData).subscribe({
      next: (res) => {
         this.imageRes = res;
         this.imageUrl = this.imageRes.image;
      },
      error: (error) => {
        this.errorService.handelError(error);
      },
    });
  }

  readURL(event: any): void {
    if (event.target.files && event.target.files[0]) {
      this.file = event.target.files[0];
      this.uploadImage(this.file);
      const reader = new FileReader();
      reader.onload = (e) => (this.imageSrc = reader.result);
      reader.readAsDataURL(this.file);

     if(this.imageUrl!='') this.getFileEvent.emit(this.imageUrl);

    }
  }
}
