import { ErrorService } from './error.service';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/shared/services/auth.service';
import { Injectable, OnInit } from '@angular/core';
import { Apis } from '../utils/apis';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { user } from './../models/user';
import { ToastrService } from 'ngx-toastr';

@Injectable({
  providedIn: 'root',
})
export class UserService {
  public apiResponse: any = {
    status: '',
    message: '',
  };

  public verifyResponse: any = {
    email: '',
  };

  public userData: user = {
    name: '',
    phone: 0,
    email: '',
    password: '',
  };

  public headers: any = new HttpHeaders({
    'Content-Type': 'application/json; charset=utf-8',
    authorization: this.getToken(),
  });

  constructor(
    private authSevices: AuthService,
    private http: HttpClient,
    private toastr: ToastrService,
    private router: Router,
    private errorService: ErrorService
  ) {
    if (authSevices.isLogedin) this.getUser();
  }

  getToken() {
    return localStorage.getItem('token') || '';
  }

  getUser() {
    this.http
      .get(Apis.user.getUserByToken, {
        headers: this.headers,
      })
      .subscribe({
        next: (res) => {
          this.userData = res as user;
        },
        error: (error) => this.errorService.handelError(error),
      });
  }

  updateUser(userData: user) {
    this.http
      .put(Apis.user.updateUserByEmail + userData.email, userData, {
        headers: this.headers,
      })
      .subscribe({
        next: (res) => {
          this.apiResponse = res;
          this.getUser();
          this.toastr.success(this.apiResponse.message, '', {
            progressBar: true,
          });
        },
        error: (error) => this.errorService.handelError(error),
      });
  }
}
