import { PropertyService } from './../../shared/services/property.service';
import { AuthService } from './../../shared/services/auth.service';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { GlobalData } from '../../shared/utils/data';
import { property } from 'src/app/shared/models/property';
import { ErrorService } from 'src/app/shared/services/error.service';

@Component({
  selector: 'app-discover',
  templateUrl: './discover.component.html',
  styles: [],
})
export class DiscoverComponent implements OnInit {
  public properties: Array<property> = [];

  public data: any = GlobalData;

  public filterData: any = {
    bed: 0,
    garage: 0,
    bath: 0,
    city: '',
    min: 0,
    max: 0,
  };

  constructor(
    private authService: AuthService,
    private activatedroute: ActivatedRoute,
    private router: Router,
    private propertyService: PropertyService,
    private errorService: ErrorService
  ) {
    this.authService.checkIsLogedIn();
  }

  ngOnInit(): void {
    this.activatedroute.queryParams.subscribe((params) => {
      if (!this.errorService.handelEmptyFilds(this.filterData, false)) {
        this.filterData = params;
      } else {
        this.properties = this.propertyService.getAllProperties();
      }
    });
  }


  setCity(value: any) {
    this.filterData.city = value as string;
  }
  setBed(value: any) {
    this.filterData.bed = value as number;
  }
  setBath(value: any) {
    this.filterData.bath = value as number;
  }
  setGarage(value: any) {
    this.filterData.garage = value as number;
  }
  setMaxPrice(value: any) {
    this.filterData.max = value as number;
  }
  setMinPrice(value: any) {
    this.filterData.min = value as number;
  }

  searchButtonPress() {
    if (!this.errorService.handelEmptyFilds(this.filterData)) {
     this.properties = this.propertyService.getPropertiesByFilter(this.filterData);
    }
    this.router.navigateByUrl(
      `?city=${this.filterData.city}&bed=${this.filterData.bed}&bath=${this.filterData.bath}&garage=${this.filterData.garage}&min=${this.filterData.min}&max=${this.filterData.max}`
    );
  }
}
