import { AuthService } from './../../shared/services/auth.service';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-discover',
  templateUrl: './discover.component.html',
  styles: [],
})
export class DiscoverComponent implements OnInit {
  login: boolean = false;

  cityList: Array<any> = ['Nabadwip', 'kolkata'];
  bedList: Array<any> = ['1', '2', '3', '4', '5'];
  bathList: Array<any> = ['1', '2', '3', '4', '5'];
  garageList: Array<any> = ['1', '2', '3'];

  selectedCity: String = '';
  selectedBed: String = '';
  selectedBath: String = '';
  selectedGarage: String = '';

  selectedPriceMax: String = '';
  selectedPriceMin: String = '';

  setCity(value: any) {
    this.selectedCity = value;
  }
  setBed(value: any) {
    this.selectedBed = value;
  }
  setBath(value: any) {
    this.selectedBath = value;
  }
  setGarage(value: any) {
    this.selectedGarage = value;
  }
  setPriceMax(value: any) {
    this.selectedPriceMax = value;
  }
  setPriceMin(value: any) {
    this.selectedPriceMin = value;
  }

  constructor(private router: Router, private authService: AuthService) {
    this.login = this.authService.login;
  }

  ngOnInit(): void {
    if (!this.login) {
      this.router.navigate(['/signin']);
    }
  }
}
