import { Injectable } from '@angular/core';
import { Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
import { AuthService } from './auth.service';

@Injectable({
  providedIn: 'root',
})
export class ErrorService {
  public apiResponse: any = {
    status: '',
    message: '',
  };

  constructor(private toastr: ToastrService) {}

  showToastW(message:string){
    this.toastr.warning(message, '', {
      progressBar: true,
      toastClass: 'top-center',
    });
  }

  handelError(error: any) {
    switch (error.status) {
      case 0: {
        this.showToastW('Connection Refused');
        break;
      }

      case 500: {
        this.toastr.warning('Internal Server Error', '', {
          progressBar: true,
          toastClass: 'top-center',
        });
        break;
      }

      case 502: {
        this.toastr.warning('Bad Gateway', '', {
          progressBar: true,
          toastClass: 'top-center',
        });
        break;
      }

      case 503: {
        this.toastr.warning('Service Unavailable', '', {
          progressBar: true,
          toastClass: 'top-center',
        });
        break;
      }

      default: {
        this.apiResponse = error.error;
        this.toastr.warning(this.apiResponse.message, '', {
          progressBar: true,
          toastClass: 'top-center',
        });
        break;
      }
    }
  }
}
