import { Injectable } from '@angular/core';
import { Apis } from '../utils/apis';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { user } from './../models/user';
import { ToastrService } from 'ngx-toastr';

@Injectable({
  providedIn: 'root',
})
export class UserService {
  public token: string = '';

  public apiResponse: any = {
    status: '',
    message: '',
  };

  public headers: any = new HttpHeaders({
    'Content-Type': 'application/json; charset=utf-8',
    authorization: getToken,
  });

  constructor(private http: HttpClient, private toastr: ToastrService) {}

  setToken() {
    this.token = localStorage.getItem('token') || '';
    console.warn(localStorage.getItem('token'));
  }

  getUser() {
    this.setToken();
    this.http.get(Apis.user.getUser, { headers: this.headers }).subscribe({
      next: (res) => {
        this.apiResponse = res;
        this.toastr.success(this.apiResponse.message, this.apiResponse.status, {
          progressBar: true,
        });
      },
      error: (error) => {
        this.apiResponse = error.error;
        this.toastr.warning(this.apiResponse.message, '', {
          progressBar: true,
        });
      },
    });
  }
}
