import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'currency',
})
export class CurrencyPipe implements PipeTransform {
  transform(value: number, ...args: unknown[]): unknown {
    if (value >= 10000000) return value.toFixed(2) / 10000000 + 'cr';

    if (value >= 100000) return value.toFixed(2) / 100000 + 'lakh';

    if (value >= 1000) return value.toFixed(2) / 1000 + 'k';

    return value;
  }
}
