import { PropertyService } from './../../shared/services/property.service';
import { AuthService } from './../../shared/services/auth.service';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { GlobalData } from '../../shared/utils/data';
import { property } from 'src/app/shared/models/property';

@Component({
  selector: 'app-discover',
  templateUrl: './discover.component.html',
  styles: [],
})
export class DiscoverComponent implements OnInit {
  public properties: Array<property> = [];

  public data: any = GlobalData;

  public filterData: any = {
    bed: null,
    garage: null,
    bath: null,
    city: null,
    min: null,
    max: null,
  };

  public filterUrl: string = '';

  constructor(
    private authService: AuthService,
    private activatedroute: ActivatedRoute,
    private router: Router,
    private propertyService: PropertyService
  ) {
    this.authService.checkIsLogedIn();
  }

  ngOnInit(): void {
    this.activatedroute.queryParams.subscribe((params) => {
      this.setFilterUrl();
      this.setFilter(params);

      if(this.filterData.bed == null) this.propertyService.getAllProperties();
      else this.propertyService.getPropertiesByFilter(this.filterUrl);
    });
  }
  ngDoCheck(): void {
    this.properties = this.propertyService.properties;
  }

  setFilter(uriData: any) {
    this.setCity(uriData.city);
    this.setBed(uriData.bed);
    this.setBath(uriData.bath);
    this.setGarage(uriData.garage);
    this.setMinPrice(uriData.min);
    this.setMaxPrice(uriData.max);

    this.setFilterUrl();
  }

  setFilterUrl() {
    this.filterUrl = `?city=${this.filterData.city}&bed=${this.filterData.bed}&bath=${this.filterData.bath}&garage=${this.filterData.garage}&min=${this.filterData.min}&max=${this.filterData.max}`;
  }

  setCity(value: any) {
    this.filterData.city = value;
  }
  setBed(value: any) {
    this.filterData.bed = value;
  }
  setBath(value: any) {
    this.filterData.bath = value;
  }
  setGarage(value: any) {
    this.filterData.garage = value;
  }
  setMaxPrice(value: any) {
    this.filterData.max = value;
  }
  setMinPrice(value: any) {
    this.filterData.min = value;
  }

  searchButtonPress() {
    this.setFilter(this.filterData);
    this.propertyService.getPropertiesByFilter(this.filterUrl);
    this.router.navigateByUrl(this.filterUrl);
  }
}
