import { property } from '../models/property';
import { Injectable } from '@angular/core';
import { Apis } from '../utils/apis';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { ErrorService } from './error.service';

@Injectable({
  providedIn: 'root',
})
export class PropertyService {
  public properties: Array<property> = [];

  public headers: any = new HttpHeaders({
    'Content-Type': 'application/json; charset=utf-8',
    authorization: this.getToken(),
  });

  constructor(private http: HttpClient, private errorService: ErrorService) {}

  getToken() {
    return localStorage.getItem('token') || '';
  }

  getAllProperties() {
    this.http
      .get(Apis.property.getAllProperties, {
        headers: this.headers,
      })
      .subscribe({
        next: (res) => {
          console.log(res);
          this.properties = res as Array<property>;
        },
        error: (error) => {
          this.errorService.handelError(error);
        },
      });
  }

  getPropertiesByFilter(data: any) {
    var uri =  `?city=${data.city}&bed=${data.bed}&bath=${data.bath}&garage=${data.garage}&min=${data.min}&max=${data.max}`;
    this.http
      .get(Apis.property.getPropertiesByFilter + uri, {
        headers: this.headers,
      })
      .subscribe({
        next: (res) => {
          console.log(res);
          this.properties = res as Array<property>;
        },
        error: (error) => {
          this.errorService.handelError(error);
        },
      });
  }


  uploadImage(data:any){
    console.log(data);
  }

  uploadProperty(data:any,image:any){
    this.uploadImage(image);
    console.log(data);

    this.http
    .get(Apis.property.uploadPropertie + uri, {
      headers: this.headers,
    })
    .subscribe({
      next: (res) => {
        console.log(res);
        this.properties = res as Array<property>;
      },
      error: (error) => {
        this.errorService.handelError(error);
      },
    });


  }

}
