import { UserService } from 'src/app/shared/services/user.service';
import { property } from '../models/property';
import { Injectable } from '@angular/core';
import { Apis } from '../utils/apis';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { ErrorService } from './error.service';
import { ToastrService } from 'ngx-toastr';

@Injectable({
  providedIn: 'root',
})
export class PropertyService {
  public properties: Array<property> = [];

  public headers: any = new HttpHeaders({
    'Content-Type': 'application/json; charset=utf-8',
    authorization: this.getToken(),
  });

  public apiResponse: any = {
    status: '',
    message: '',
  };

  constructor(
    private http: HttpClient,
    private toastr: ToastrService,
    private errorService: ErrorService
  ) {}

  getToken() {
    return localStorage.getItem('token') || '';
  }

  getAllProperties()any {
    this.http
      .get(Apis.property.getAllProperties, {
        headers: this.headers,
      })
      .subscribe({
        next: (res) => {
          this.properties = res as Array<property>;
        },
        error: (error) => {
          this.errorService.handelError(error);
        },
      });
  }

  getPropertiesByFilter(data: any): any {
    var uri = `?city=${data.city}&bed=${data.bed}&bath=${data.bath}&garage=${data.garage}&min=${data.min}&max=${data.max}`;
    this.http
      .get(Apis.property.getPropertiesByFilter + uri, {
        headers: this.headers,
      })
      .subscribe({
        next: (res) => {
          return res as Array<property>;
        },
        error: (error) => {
          this.errorService.handelError(error);
        },
      });
  }

  uploadProperty(data: any) {
    console.log(data);

    this.http
      .post(Apis.property.uploadPropertie, data, {
        headers: this.headers,
      })
      .subscribe({
        next: (res) => {
          this.apiResponse = res;
          this.toastr.success(this.apiResponse.message, '', {
            progressBar: true,
          });
        },
        error: (error) => {
          this.errorService.handelError(error);
        },
      });
  }

  getPropertiesByEmail(email: string): any{
    this.http
      .get(Apis.property.getPropertiesByEmail + email, {
        headers: this.headers,
      })
      .subscribe({
        next: (res) => {
          return res as Array<property>;
        },
        error: (error) => {
          this.errorService.handelError(error);
        },
      });
  }
}
